<template>    <div class="new-question">        <div class="give-question-wrapper">            <h3>题目</h3>            <textarea                    cols="50"                    rows="10"                    placeholder="点此输入问题"                    spellcheck="true"                    class="inputAnswer-text"                    v-model="newQuestion"            ></textarea>        </div>        <div class="give-answer-wrapper">            <h3>参考答案</h3>            <textarea                    cols="50"                    rows="10"                    placeholder="点此输入参考答案"                    spellcheck="true"                    class="inputAnswer-text"                    v-model="newAnswer"            ></textarea>        </div>        <div class="give-button">            <button @click="submit">提交</button>        </div>    </div></template><script>    export default {        name: "newQuestion",        data() {            return {                newAnswer: "",                newQuestion: "",                type: "1",                id: 0,            };        },        mounted() {            this.getQuestionDetail()        },        methods: {            async submit() {                await this.axios.post("http://49.235.128.42:3000/question", {                    id: this.id,                    question: this.newQuestion,                    answer: this.newAnswer,                    type: this.type,                }).then(()=>{alert('提交成功')},()=>{alert('提交失败')})                this.id++                this.newAnswer = ''                this.newQuestion=''            },            async getQuestionDetail() {                let {data} = await this.axios.get('http://49.235.128.42:3000/question')                this.id = data.length            },        }    };</script><style lang="scss" scoped>    .give-button {        margin-top: 20px;    }</style>