<template>    <div class="new-question">        <q-title></q-title>        <div class="give-question-wrapper">            <h3>题目</h3>            <textarea                    cols="50"                    rows="10"                    placeholder="点此输入问题"                    spellcheck="true"                    class="inputAnswer-text"                    v-model="newQuestion"            ></textarea>        </div>        <div class="give-answer-wrapper">            <h3>参考答案</h3>            <textarea                    cols="50"                    rows="10"                    placeholder="点此输入参考答案"                    spellcheck="true"                    class="inputAnswer-text"                    v-model="newAnswer"            ></textarea>        </div>        <div class="give-button">            <button @click="submit">提交</button>        </div>    </div></template><script>    import qTitle from "./qTitle";    export default {        name: "newQuestion",        components:{            qTitle,        },        data() {            return {                newAnswer: "",                newQuestion: "",                type: "1",                id: 0,            };        },        mounted() {            this.getQuestionDetail()            console.log(process.env.NODE_ENV);        },        methods: {            async submit() {                const isDev = process.env.NODE_ENV === 'development'                const path = isDev ? 'http://localhost:3000/question' : 'http://172.17.0.4:3000/question'                await this.axios.post(path, {                    id: this.id,                    question: this.newQuestion,                    answer: this.newAnswer,                    type: this.type                })                this.id++                this.newAnswer = ''                this.newQuestion=''            },            async getQuestionDetail() {                const isDev = process.env.NODE_ENV === 'development'                const path = isDev ? 'http://localhost:3000/question' : 'http://172.17.0.4:3000/question'                let {data} = await this.axios.get(path)                this.id = data.length + 1            },        }    };</script><style lang="scss" scoped>    .give-button {        margin-top: 20px;    }</style>