<template>    <div class="q-title">        <div class="title-back" @click="backHome"><span>{{qback}}</span></div>        <h4 class="title-text">{{title}}</h4>        <div :class="isWrong ? 'title-wrong' : 'title-wrong-transparent'"><span>错误反馈</span></div>    </div></template><script>    export default {        name: "q-title",        data(){            return{                isWrong:true            }        },        methods:{            backHome(){                this.$router.go(-1)            }        },        computed:{            qback(){                return this.$route.name === "question"?  "结束刷题":"返回主页"            },            title(){                return this.$route.name === "question"?  "前端刷题":"贡献题库"            },        },        mounted() {            this.$route.name === "question" ? this.isWrong = true :this.isWrong = false        }    }</script><style lang="scss" scoped>.q-title{    display: flex;    .title-back,.title-text,.title-wrong,.title-wrong-transparent{        flex: 1;        display: flex;        justify-content: center;        align-items: center;    }    .title-back  ,.title-wrong, .title-wrong-transparent{        span{            padding: 5px;            border: 1px solid black;            border-radius: 5px;        }    }    .title-wrong-transparent{        opacity: 0;    }    button{        font-size: 14px;    }}</style>