<template>    <div class="question">        <q-title></q-title>        <div class="question-wrapper">            <h3>题目</h3>            <p>{{question}}</p>        </div>        <div class="inputAnswer-wrapper" v-show="type !== 1">            <textarea                    id="inputAnswer"                    v-show="isEdit"                    v-model="content"                    placeholder="点此输入答案"                    spellcheck="true"                    class="inputAnswer-text"></textarea>            <div v-show="!isEdit" class="contentBox">                {{content}}            </div>        </div>        <div class="btns">            <button @click="isEdit = !isEdit">{{anwserQuestion}}</button>            <div class="btn-wrapper">                <button @click="preItem" :disabled="preDisable" :class="{disable: preDisable}">上一题</button>                <button @click="nextItem" :disabled="nextDisable" :class="{disable: nextDisable}">下一题</button>                <button @click="showAnswer">{{activeText}}参考答案</button>            </div>        </div>        <div class="answer-wrapper" v-show="answerVisible">            <p>{{answer}}</p>        </div>    </div></template><script>    import _ from 'lodash'    import qTitle from './qTitle'    export default {        name: "q-Question",        components:{            qTitle,        },        data() {            return {                questions: [],                orders: [],                idx: 0,                isEdit: true,                // question: '',                // type: '',                // answer: '',                answerVisible: false,                active: '查看',                activeMap: {},                content:'',            }        },        computed: {            anwserQuestion(){              return this.isEdit? '回答完毕':'编辑回答'            },            preDisable() {                return this.idx === 0            },            nextDisable() {              return this.idx === this.questions.length - 1 || this.idx === this.max            },            question () {                return this.questions[this.idx] && this.questions[this.idx].question            },            type () {                return this.questions[this.idx] && this.questions[this.idx].type            },            activeText () {                return this.answerVisible ? '关闭' : '查看'            },            answer(){                return this.questions[this.idx] && this.questions[this.idx].answer            },        },        watch: {            idx () {                this.getQuestionDetail()            }        },        methods: {            async getQuestionDetail() {                let {data} = await this.axios.get('http://localhost:3000/question')                this.questions = _.shuffle(data).slice(0, this.$route.params.num)            },            preItem() {                this.idx--            },            nextItem() {                this.idx++                this.answerVisible = false            },            showAnswer() {                this.answerVisible = !this.answerVisible            }        },        mounted() {            this.getQuestionDetail()        },    }</script><style lang="scss" scoped>    .question {        .question-wrapper {        }        .inputAnswer-wrapper {            textarea{                font-size: 14px;            }            .btn-wrapper{            }            #inputAnswer {                display: block;                box-sizing: border-box;                width: 100%;                height: 200px;                border: 1px solid #333;                outline: none;                resize: none;                padding: 6px;                font-size: 16px;            }                .contentBox {                    font-size: 16px;                    box-sizing: border-box;                    padding: 6px;                    height: 200px;                    text-align: left;                }        }        .answer-wrapper {            background-color: #eee;        }    }button{    margin: 5px;}</style>