<template>    <div class="welcome">        <div class="welcome-title">            <h2>欢迎使用初级前端刷题系统</h2>        </div>        <div class="btns">            <div class="btn-start">                <h5>选择题目数量然后开始答题</h5>                <div class="select-questionNum">                    <label>                        <input type="radio" v-model="questionNum" :value="5"/>5题                    </label>                    <label>                        <input type="radio" v-model="questionNum" :value="10"/>10题                    </label>                    <label>                        <input type="radio" v-model="questionNum" :value="15"/>15题                    </label>                    <label>                        <input type="radio" v-model="questionNum" :value="20"/>20题                    </label>                </div>                <button @click="toQuestion">开始答题</button>            </div>            <div class="btn-give">                <button @click="toNewQuestion">贡献题库</button>            </div>        </div>    </div></template><script>    import question from "../components/question";    export default {        name: "welcome",        data() {            return {                questionNum: 10            }        },        methods: {            toQuestion() {                this.$router.push({                    name: 'question',                    params: {                        num: this.questionNum                    }                });            },            toNewQuestion() {                this.$router.push("newQuestion");            }        }    };</script><style lang="scss" scoped>    .welcome {        position: relative;        margin-top: 40px;        .btns {            margin-top: 60px;            width: 100%;            display: flex;            flex-direction: column;            div {                padding: 10px            }            .btn-start,            .btn-give {                flex: 1;                display: flex;                justify-content: center;                align-items: center;                flex-direction: column;            }        }    }</style>